sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {
	},
	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		//var testdate = new GlideDateTime(strDate);
		var testNowDate = strDate.split(" ");
		var rightNowString = rightnow.toString();
		var rightNowDate = rightNowString.split(" ");
		gs.warn ("rightNowDate: " + rightNowDate + "; testNowDateaaaa: " + testNowDate);
		// If the testdate is before rightnow, return true, else return false
		if (testNowDate[0] < (rightNowDate[0])) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate)
	{
	var rightnow = new GlideDateTime();	
	var rightNowDateString = rightnow.toString();
	var rightNowDate = rightNowDateString.split(" ");
	//var whenNeededString = whenNeeded.toString();
	//var whenNeededDate = whenNeededString.split(" ");
	var whenNeededDate = strDate.split(" ");

	if(whenNeededDate[0].equals(rightNowDate[0]))
		{
		return true;
		}
	else
		{
		return false;
		}
	},
	type: 'NeedItUtils'
};
